  //   } catch (error) {
  //     this.setState({ errorMessage: error });
  //   }
  // }

  render() {
    return (
      <div>
        <Item
          name="hi"
          price="200"
          image="https://i.imgur.com/fmUsxCO.jpg"
          category="Shoes"
          quantity={this.state.quantity}
          onClick={id => this.handleClick(id)}
        />
      </div>
    );
  }
}
